  async function uploadVideo(file) {
    const fd = new FormData();
    fd.append('video', file);
    const res = await fetch('/api/upload-video', { method: 'POST', body: fd });
    const json = await res.json();
    if (json.error) throw new Error(json.error);
    setVideoFile(json.file);
    // probe metadata for fps/duration
    try {
      const pr = await fetch(`/api/probe/${json.file}`);
      const m = await pr.json();
      if (!m.error) setMeta({ fps: Math.max(1, Math.round(m.fps||30)), duration: Math.max(1, Math.floor(m.duration||5)), width: m.width||1080, height: m.height||1920 });
    } catch (e) {
      console.warn('probe failed', e);
    }
  }

